import React, { useState, useEffect } from "react";
import type { ModalProps } from "@mantine/core";
import {
  Modal,
  Stack,
  Text,
  TextInput,
  Button,
  Flex,
  CloseButton,
  Group,
  Alert,
  Textarea,
} from "@mantine/core";
import { MdWarning } from "react-icons/md";

import React, { useState } from "react";
import styled from "styled-components";
import { useComputedColorScheme, Tooltip, ActionIcon } from "@mantine/core";
import { MdEdit } from "react-icons/md";
import type { NodeProps } from "reaflow";
import { Node } from "reaflow";
import { useModal } from "../../../../../store/useModal";
import type { NodeData } from "../../../../../types/graph";
import useGraph from "../stores/useGraph";
import { ObjectNode } from "./ObjectNode";
import { TextNode } from "./TextNode";

const NodeWrapper = styled.div`
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;

  &:hover .edit-button {
    opacity: 1;
  }
`;

const EditButtonWrapper = styled.div`
  position: absolute;
  top: 4px;
  right: 4px;
  opacity: 0;
  transition: opacity 0.2s ease-in-out;
  pointer-events: all;
  z-index: 10;
`;

const CustomNodeWrapper = (nodeProps: NodeProps<NodeData>) => {
  const setSelectedNode = useGraph(state => state.setSelectedNode);
  const setVisible = useModal(state => state.setVisible);
  const colorScheme = useComputedColorScheme();
  const [showEditButton, setShowEditButton] = useState(false);

  const handleNodeClick = React.useCallback(
    (_: React.MouseEvent<SVGGElement, MouseEvent>, data: NodeData) => {
      if (setSelectedNode) setSelectedNode(data);
      setVisible("NodeModal", true);
    },
    [setSelectedNode, setVisible]
  );

  const handleEditClick = (e: React.MouseEvent, data: NodeData) => {
    e.stopPropagation();
    if (setSelectedNode) setSelectedNode(data);
    setVisible("EditNodeModal", true);
  };

  // Check if node has editable fields (not arrays or objects)
  const hasEditableFields = nodeProps.properties.text.some(
    row => row.type !== "array" && row.type !== "object"
  );

  return (
    <Node
      {...nodeProps}
      onClick={handleNodeClick as any}
      animated={false}
      label={null as any}
      onEnter={ev => {
        ev.currentTarget.style.stroke = "#3B82F6";
        setShowEditButton(true);
      }}
      onLeave={ev => {
        ev.currentTarget.style.stroke = colorScheme === "dark" ? "#424242" : "#BCBEC0";
        setShowEditButton(false);
      }}
      style={{
        fill: colorScheme === "dark" ? "#292929" : "#ffffff",
        stroke: colorScheme === "dark" ? "#424242" : "#BCBEC0",
        strokeWidth: 1,
      }}
    >
      {({ node, x, y }) => {
        const hasKey = nodeProps.properties.text[0].key;
        const nodeComponent = !hasKey ? (
          <TextNode node={nodeProps.properties as NodeData} x={x} y={y} />
        ) : (
          <ObjectNode node={node as NodeData} x={x} y={y} />
        );

        return (
          <NodeWrapper>
            {nodeComponent}
            {hasEditableFields && showEditButton && (
              <EditButtonWrapper className="edit-button">
                <Tooltip label="Edit value" position="top">
                  <ActionIcon
                    size="sm"
                    variant="filled"
                    color="blue"
                    onClick={e => handleEditClick(e, nodeProps.properties as NodeData)}
                  >
                    <MdEdit size={14} />
                  </ActionIcon>
                </Tooltip>
              </EditButtonWrapper>
            )}
          </NodeWrapper>
        );
      }}
    </Node>
  );
};

export { DownloadModal } from "./DownloadModal";
export { ImportModal } from "./ImportModal";
export { NodeModal } from "./NodeModal";
export { EditNodeModal } from "./EditNodeModal";
export { JWTModal } from "./JWTModal";
export { SchemaModal } from "./SchemaModal";
export { JQModal } from "./JQModal";
export { TypeModal } from "./TypeModal";
export { JPathModal } from "./JPathModal";

