import type { ModalProps } from "@mantine/core";
import {
  Modal,
  Stack,
  Text,
  TextInput,
  Button,
  Flex,
  CloseButton,
  Group,
  Alert,
  Textarea,
} from "@mantine/core";
import { IconAlertCircle } from "@tabler/icons-react";
import type { NodeData, NodeRow } from "../../../types/graph";
import useGraph from "../../editor/views/GraphView/stores/useGraph";
import useJson from "../../../store/useJson";
import { updateJsonValue } from "../../../lib/utils/jsonEditor";

export const EditNodeModal = ({ opened, onClose }: ModalProps) => {
  const nodeData = useGraph(state => state.selectedNode);
  const editNode = useGraph(state => state.editNode);
  const json = useJson(state => state.json);
  const setJson = useJson(state => state.setJson);

  const [editValues, setEditValues] = useState<Record<string, string>>({});
  const [error, setError] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (opened && nodeData) {
      const values: Record<string, string> = {};
      nodeData.text.forEach(row => {
        if (row.type !== "array" && row.type !== "object") {
          const key = row.key ?? "value";
          values[key] = String(row.value);
        }
      });
      setEditValues(values);
      setError(null);
    }
  }, [opened, nodeData]);

  const handleValueChange = (key: string, value: string) => {
    setEditValues(prev => ({ ...prev, [key]: value }));
    setError(null);
  };

  const handleSave = async () => {
    if (!nodeData) return;

    try {
      setIsSubmitting(true);
      setError(null);

      // Validate and parse values
      const updatedText: NodeRow[] = nodeData.text.map(row => {
        if (row.type === "array" || row.type === "object") {
          return row;
        }

        const key = row.key ?? "value";
        const newValueStr = editValues[key];

        // Try to parse as JSON first (for valid JSON values like numbers, booleans, null)
        let newValue: any = newValueStr;
        try {
          newValue = JSON.parse(newValueStr);
        } catch {
          // If parsing fails, treat as string
          newValue = newValueStr;
        }

        return {
          ...row,
          value: newValue,
        };
      });

      // Update node in graph
      editNode(nodeData.id, { text: updatedText });

      // Update the JSON data
      const updatedJson = updateJsonValue(
        json,
        nodeData.path || [],
        updatedText[0].value
      );
      setJson(updatedJson);

      onClose();
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to save changes");
      setIsSubmitting(false);
    }
  };

  const handleCancel = () => {
    setEditValues({});
    setError(null);
    onClose();
  };

  if (!nodeData) return null;

  const editableFields = nodeData.text.filter(row => row.type !== "array" && row.type !== "object");

  return (
    <Modal size="sm" opened={opened} onClose={handleCancel} centered withCloseButton={false}>
      <Stack pb="sm" gap="md">
        <Flex justify="space-between" align="center">
          <Text fz="md" fw={600}>
            Edit Node
          </Text>
          <CloseButton onClick={handleCancel} />
        </Flex>

        {error && (
          <Alert icon={<IconAlertCircle size={16} />} color="red" title="Error">
            {error}
          </Alert>
        )}

        <Stack gap="sm">
          {editableFields.map((row, index) => {
            const key = row.key ?? "value";
            const isMultiline = String(row.value).length > 50;

            return (
              <div key={`${nodeData.id}-${index}`}>
                <Text fz="xs" fw={500} mb="xs">
                  {key}
                </Text>
                {isMultiline ? (
                  <Textarea
                    value={editValues[key] || ""}
                    onChange={e => handleValueChange(key, e.currentTarget.value)}
                    placeholder={`Enter ${key} value`}
                    minRows={3}
                    maxRows={8}
                  />
                ) : (
                  <TextInput
                    value={editValues[key] || ""}
                    onChange={e => handleValueChange(key, e.currentTarget.value)}
                    placeholder={`Enter ${key} value`}
                  />
                )}
              </div>
            );
          })}
        </Stack>

        <Group justify="flex-end" mt="md">
          <Button variant="default" onClick={handleCancel} disabled={isSubmitting}>
            Cancel
          </Button>
          <Button onClick={handleSave} loading={isSubmitting}>
            Save
          </Button>
        </Group>
      </Stack>
    </Modal>
  );
};
"}

{"content": "/**
 * Utility function to update a JSON value at a specific path
 */

export function updateJsonValue(json: string, path: (string | number)[], newValue: any): string {
  try {
    const obj = JSON.parse(json);

    if (path.length === 0) {
      // Root level update
      return JSON.stringify(newValue, null, 2);
    }

    // Navigate to the parent object
    let current = obj;
    for (let i = 0; i < path.length - 1; i++) {
      const key = path[i];
      current = current[key];
    }

    // Update the value
    const lastKey = path[path.length - 1];
    current[lastKey] = newValue;

    return JSON.stringify(obj, null, 2);
  } catch (err) {
    throw new Error(`Failed to update JSON value: ${err instanceof Error ? err.message : "Unknown error"}`);
  }
}
