          current = current[path[i]];
        }
        
        // Update the value
        if (path.length > 0) {
          current[path[path.length - 1]] = valueToUpdate;
        } else {
          // Root level update
          Object.assign(obj, valueToUpdate);
        }
        
        const updatedJson = JSON.stringify(obj, null, 2);
        
        // Update JSON store directly (not using setJson which would trigger re-parse)
        useJson.setState({ json: updatedJson, loading: false });
      } catch (err) {
        throw new Error("Failed to update JSON");
      }

      onClose();
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to save changes");
      setIsSubmitting(false);
    }
  };

