import { MdWarning } from "react-icons/md";
import type { NodeData, NodeRow } from "../../../types/graph";
import useGraph from "../../editor/views/GraphView/stores/useGraph";
import useJson from "../../../store/useJson";
import useFile from "../../../store/useFile";

// Update JSON without triggering graph re-parse
try {
  const obj = JSON.parse(json);
  
  let current = obj;
  const path = nodeData.path || [];
  
  // Navigate to the parent
  for (let i = 0; i < path.length - 1; i++) {
    current = current[path[i]];
  }
  
  // Update the value
  if (path.length > 0) {
    current[path[path.length - 1]] = valueToUpdate;
  } else {
    // Root level update
    Object.assign(obj, valueToUpdate);
  }
  
  const updatedJson = JSON.stringify(obj, null, 2);
  
  // Update both JSON store and File store (for editor sync)
  useJson.setState({ json: updatedJson, loading: false });
  useFile.setState({ contents: updatedJson, hasChanges: true });
} catch (err) {
  throw new Error("Failed to update JSON");
}

