    try {
      setIsSubmitting(true);
      setError(null);

      // Get all the edited values
      let valueToUpdate: any;
      const editableRows = nodeData.text.filter(row => row.type !== "array" && row.type !== "object");
      
      if (editableRows.length === 1 && !editableRows[0].key) {
        // Single value node
        const newValueStr = editValues[editableRows[0].key ?? "value"];
